			if (la.kind == 90) {
				GenericParameters(typeArgs, true);
			}
			Formals(true, true, false, false, ins);
			if (la.kind == 111 || la.kind == 112) {
				if (la.kind == 111) {
					Get();
				} else {
					Get();
