					Get();
					x = t; 
					if (la.kind == _assume) {
						Expect(35);
						var tok = t; 
						while (la.kind == 83) {
							Attribute(ref tokenAttrs);
						}
						suchThatAssume = new AttributedToken(tok, tokenAttrs); 
					}
					Expression(out suchThat, false, true);
				} else if (la.kind == 120) {
