				} else SynErr(220);
				Expect(33);
				endTok = t; 
			} else if (la.kind == 24) {
				Get();
				SemErr(t, "invalid statement beginning here (is a 'label' keyword missing? or a 'const' or 'var' keyword?)"); 
			} else SynErr(221);
		} else if (la.kind == 120) {
			Get();
			x = t; startToken = t; 
			if (IsAssumeTypeKeyword(la)) {
				if (la.kind == 101) {
					Get();
				} else if (la.kind == 36) {
					Get();
