	void DotSuffix(out IToken x, out IToken y) {
		Contract.Ensures(Contract.ValueAtReturn(out x) != null);
		x = Token.NoToken;
		y = null;
		
		if (StartOf(8)) {
			Ident(out x);
		} else if (la.kind == 2) {
			Get();
			x = t; 
		} else if (la.kind == 4) {
			Get();
			x = t;
			int exponent = x.val.IndexOf('e');
			if (0 <= exponent) {
			 // this is not a legal field/destructor name
			 // Also it is not currently reachable, because decimaldigits does not yet support exponents
			 SemErr(x, "invalid name after a '.'");
			} else {
			 int dot = x.val.IndexOf('.');
			 if (0 <= dot) {
			   // change token
			   // [prev][    x    ]
			   // to
			   // [prev][y][dot][x]
			   y = new Token();
			   y.pos = x.pos;
			   y.val = x.val.Substring(0, dot);
			   y.col = x.col;
			   y.line = x.line;
			   y.Filename = x.Filename;
			   y.kind = x.kind;
			   
			   var dotTok = new Token();
			   dotTok.pos = x.pos + dot;
			   dotTok.val = ".";
			   dotTok.Filename = x.Filename;
			   dotTok.line = x.line;
			   dotTok.kind = x.kind;
			   dotTok.col = x.col + dot;
			   
			   x.pos = x.pos + dot + 1;
			   x.val = x.val.Substring(dot + 1);
			   x.col = x.col + dot + 1;
			   
			   var prev = x.Prev;
			   
			   prev.Next = y;
			   y.Prev = prev;
			   
			   y.Next = dotTok;
			   dotTok.Prev = y;
			   
			   dotTok.Next = x;
			   x.Prev = dotTok;
			   
